local sqlite3 = require("lsqlite3")
local db = sqlite3.open_memory()
local vacuum_command = string.format("VACUUM INTO \"%s\"", arg[1])

-- определение структуры бд

db:exec[[
create table псалтирь (
   псалом       INTEGER not null,
   часть        INTEGER not null,
   кафизма      INTEGER not null,
   слава        INTEGER not null,
   текст_ру     TEXT    not null,
   текст_цс     TEXT    not null,
   заголовок_ру TEXT            ,
   заголовок_цс TEXT            ,
   constraint псалом_id primary key (псалом,часть)
)   ;

create table апостол (
   комментарий TEXT    PRIMARY KEY,
   зачало      INTEGER not null,
   возглас_id  INTEGER not null,
   текст_ру    TEXT    not null,
   текст_цс    TEXT    not null
)   ;

create table евангелие (
   комментарий TEXT    PRIMARY KEY,
   зачало      INTEGER not null,
   возглас_id  INTEGER not null,
   текст_ру    TEXT    not null,
   текст_цс    TEXT    not null
)   ;

create table возглас (
   возглас_id  INTEGER PRIMARY KEY,
   текст_ру    TEXT    not null,
   текст_цс    TEXT    not null,
   комментарий TEXT    not null unique
)   ;
]]

-- определение методов добавления данных в базу

function add_psalom(t)
  db:exec(
    string.format("INSERT INTO псалтирь VALUES (%d, %d, %d, %d, \"%s\", \"%s\", \"%s\", \"%s\");",
                    t["псалом"],
                    t["часть"],
                    t["кафизма"],
                    t["слава"],
                    t["текст_ру"],
                    t["текст_цс"],
                    t["заголовок_ру"],
                    t["заголовок_цс"]
    )
  )
end

function add_vozglas(t)
  db:exec(
    string.format("INSERT INTO возглас VALUES (NULL, \"%s\", \"%s\", \"%s\");",
                    t["текст_ру"],
                    t["текст_цс"],
                    t["комментарий"]
    )
  )
end

function add_apostol_evangelie(t, table_name)
  local comment = t["комментарий"]
  local vozglas_id = 0
  local query = 'SELECT возглас_id FROM возглас WHERE комментарий="'..string.match(comment, "^[^%.]+")..'"'
  for i in db:urows(query) do vozglas_id = i end
  assert(vozglas_id ~= 0)
  db:exec(
    string.format("INSERT INTO %s VALUES (\"%s\", %d, %d, \"%s\", \"%s\");",
                    table_name,
                    comment,
                    t["зачало"],
                    vozglas_id,
                    t["текст_ру"],
                    t["текст_цс"]
    )
  )
end

function add_apostol(t)
  add_apostol_evangelie(t, "апостол")
end

function add_evangelie(t)
  add_apostol_evangelie(t, "евангелие")
end

