}

add_psalom {
  ["псалом"] = 47,
  ["часть"] = 1,
  ["кафизма"] = 7,
  ["слава"] = 1,
  ["текст_ру"] = "Ве́лий Госпо́дь и хва́лен зело́ во гра́де Бо́га на́шего, в горе́ святе́й Его́. Благокоре́нным ра́дованием всея́ земли́: го́ры Сио́нския, ре́бра се́верова, град Царя́ вели́каго. Бог в тя́жестех eго́ зна́емь есть, егда́ заступа́ет и́. Я́ко се ца́рие зе́мстии собра́шася, снидо́шася вку́пе. Ти́и ви́девше та́ко, удиви́шася, смято́шася, подвиго́шася. Тре́пет прия́т я́: та́мо боле́зни я́ко ражда́ющия. Ду́хом бу́рным сокруши́ши корабли́ Фарси́йския. Я́коже слы́шахом, та́ко и ви́дехом во гра́де Го́спода Сил, во гра́де Бо́га на́шего, Бог основа́ и́ в век. Прия́хом, Бо́же, ми́лость Твою́ посреде́ люде́й Твои́х. По и́мени Твоему́, Бо́же, та́ко и хвала́ Твоя́ на конца́х земли́, пра́вды испо́лнь десни́ца Твоя́. Да возвесели́тся гора́ Сио́нская и да возра́дуются дще́ри Иуде́йския суде́б ра́ди Твои́х, Го́споди. Обыди́те Сио́н и обыми́те eго́, пове́дите в столпе́х eго́. Положи́те сердца́ ва́ша в си́лу Его́ и раздели́те до́мы Его́, я́ко да пове́сте в ро́де ино́м. Я́ко Той есть Бог наш во век и в век ве́ка, Той упасе́т нас во ве́ки.",
  ["текст_цс"] = "Ве́лїй гдⷭ҇ь и҆ хва́ленъ ѕѣлѡ̀ во гра́дѣ бг҃а на́шегѡ, въ горѣ̀ ст҃ѣ́й є҆гѡ̀, Бл҃гокоре́ннымъ ра́дованїемъ всеѧ̀ землѝ: го́ры сїѡ́нскїѧ, ре́бра сѣ́верѡва, гра́дъ цр҃ѧ̀ вели́кагѡ. Бг҃ъ въ тѧ́жестѣхъ є҆гѡ̀ зна́емь є҆́сть, є҆гда̀ застꙋпа́етъ и҆̀. Ꙗ҆́кѡ сѐ, ца́рїе зе́мстїи собра́шасѧ, снидо́шасѧ вкꙋ́пѣ: Ті́и ви́дѣвше та́кѡ, ᲂу҆диви́шасѧ, смѧто́шасѧ, подвиго́шасѧ: Тре́петъ прїѧ́тъ ѧ҆̀ та́мѡ, бѡлѣ́зни ꙗ҆́кѡ ражда́ющїѧ. Дꙋ́хомъ бꙋ́рнымъ сокрꙋши́ши кораблѝ ѳарсі̑йскїѧ. Ꙗ҆́коже слы́шахомъ, та́кѡ и҆ ви́дѣхомъ во гра́дѣ гдⷭ҇а си́лъ, во гра́дѣ бг҃а на́шегѡ: бг҃ъ ѡ҆снова̀ и҆ въ вѣ́къ. Прїѧ́хомъ, бж҃е, млⷭ҇ть твою̀ посредѣ̀ люде́й твои́хъ. По и҆́мени твоемꙋ̀, бж҃е, та́кѡ и҆ хвала̀ твоѧ̀ на конца́хъ землѝ: пра́вды и҆спо́лнь десни́ца твоѧ̀. Да возвесели́тсѧ гора̀ сїѡ́нскаѧ, и҆ да возра́дꙋютсѧ дщє́ри і҆ꙋдє́йскїѧ, сꙋде́бъ ра́ди твои́хъ, гдⷭ҇и. Ѡ҆быди́те сїѡ́нъ и҆ ѡ҆быми́те є҆го̀, повѣ́дите въ столпѣ́хъ є҆гѡ̀: Положи́те сердца̀ ва̑ша въ си́лꙋ є҆гѡ̀, и҆ раздѣли́те до́мы є҆гѡ̀, ꙗ҆́кѡ да повѣ́сте въ ро́дѣ и҆нѣ́мъ. Ꙗ҆́кѡ то́й є҆́сть бг҃ъ на́шъ во вѣ́къ и҆ въ вѣ́къ вѣ́ка: то́й ᲂу҆пасе́тъ на́съ во вѣ́ки.",
  ["заголовок_ру"] = "Псало́м, пе́сни сыно́в Коре́овых, вторы́я суббо́ты.",
  ["заголовок_цс"] = "Ѱало́мъ пѣ́сни сынѡ́въ коре́овыхъ, вторы́ѧ сꙋббѡ́ты."
}

add_psalom {
  ["псалом"] = 48,
  ["часть"] = 1,
  ["кафизма"] = 7,
  ["слава"] = 1,
  ["текст_ру"] = "Услы́шите сия́ вси язы́цы, внуши́те вси живу́щии по вселе́нней: земноро́днии же и сы́нове челове́честии, вку́пе бога́т и убо́г. Уста́ моя́ возглаго́лют прему́дрость, и поуче́ние се́рдца моего́ ра́зум. Приклоню́ в при́тчу у́хо мое́, отве́рзу во псалти́ри гана́ние мое́. Вску́ю бою́ся, в день лют? Беззако́ние пяты́ моея́ обы́дет мя. Наде́ющиися на си́лу свою́ и о мно́жестве бога́тства своего́ хва́лящиися, брат не изба́вит, изба́вит ли челове́к? Не даст Бо́гу изме́ны за ся, и це́ну избавле́ния души́ своея́, и утруди́ся в век. И жив бу́дет до конца́, не у́зрит па́губы. Егда́ уви́дит прему́дрыя умира́ющыя, вку́пе безу́мен и несмы́слен поги́бнут и оста́вят чужди́м бога́тство свое́. И гро́би их жили́ща их во век, селе́ния их в род и род, нареко́ша имена́ своя́ на земля́х. И челове́к в че́сти сый не разуме́, приложи́ся ското́м несмы́сленным и уподо́бися им. Сей путь их собла́зн им, и по сих во усте́х своих благоволя́т. Я́ко о́вцы во а́де положе́ни суть, смерть упасе́т я́, и облада́ют и́ми пра́вии зау́тра, и по́мощь их обетша́ет во а́де, от сла́вы своея́ изринове́ни бы́ша. Оба́че Бог изба́вит ду́шу мою́ из руки́ а́довы, егда́ прие́млет мя. Не убо́йся, егда́ разбогате́ет челове́к или́ егда́ умно́жится сла́ва до́му eго́: я́ко внегда́ умре́ти eму́, не во́змет вся, ниже́ сни́дет с ним сла́ва eго́. Я́ко душа́ eго́ в животе́ eго́ благослови́тся: испове́стся Тебе́, егда́ благосотвори́ши eму́. Вни́дет да́же до ро́да оте́ц свои́х, да́же до ве́ка не у́зрит све́та. И челове́к в че́сти сый не разуме́, приложи́ся ското́м несмы́сленным и уподо́бися им.",
  ["текст_цс"] = "Оу҆слы́шите сїѧ̑, всѝ ꙗ҆зы́цы, внꙋши́те, всѝ живꙋ́щїи по вселе́ннѣй, Земноро́днїи же и҆ сы́нове человѣ́честїи, вкꙋ́пѣ бога́тъ и҆ ᲂу҆бо́гъ. Оу҆ста̀ моѧ̑ возглаго́лютъ премꙋ́дрость, и҆ поꙋче́нїе се́рдца моегѡ̀ ра́зꙋмъ. Приклоню̀ въ при́тчꙋ ᲂу҆́хо моѐ, ѿве́рзꙋ во ѱалти́ри гана́нїе моѐ. Вскꙋ́ю бою́сѧ въ де́нь лю́тъ; беззако́нїе пѧты̀ моеѧ̀ ѡ҆бы́детъ мѧ̀. Надѣ́ющїисѧ на си́лꙋ свою̀ и҆ ѡ҆ мно́жествѣ бога́тства своегѡ̀ хва́лѧщїисѧ: Бра́тъ не и҆зба́витъ, и҆зба́витъ ли человѣ́къ; не да́стъ бг҃ꙋ и҆змѣ́ны за сѧ̀, И҆ цѣ́нꙋ и҆збавле́нїѧ дꙋшѝ своеѧ̀: и҆ ᲂу҆трꙋди́сѧ въ вѣ́къ, И҆ жи́въ бꙋ́детъ до конца̀, не ᲂу҆́зритъ па́гꙋбы. Є҆гда̀ ᲂу҆ви́дитъ премꙋ̑дрыѧ ᲂу҆мира́ющыѧ, вкꙋ́пѣ безꙋ́менъ и҆ несмы́сленъ поги́бнꙋтъ, и҆ ѡ҆ста́вѧтъ чꙋжди̑мъ бога́тство своѐ. И҆ гро́би и҆́хъ жили̑ща и҆́хъ во вѣ́къ, селє́нїѧ и҆́хъ въ ро́дъ и҆ ро́дъ, нареко́ша и҆мена̀ своѧ̑ на землѧ́хъ. И҆ человѣ́къ въ че́сти сы́й не разꙋмѣ̀, приложи́сѧ скотѡ́мъ несмы́слєннымъ и҆ ᲂу҆подо́бисѧ и҆̀мъ. Се́й пꙋ́ть и҆́хъ собла́знъ и҆̀мъ, и҆ по си́хъ во ᲂу҆стѣ́хъ свои́хъ бл҃говолѧ́тъ. Ꙗ҆́кѡ ѻ҆́вцы во а҆́дѣ положе́ни сꙋ́ть, сме́рть ᲂу҆пасе́тъ ѧ҆̀: и҆ ѡ҆блада́ютъ и҆́ми пра́вїи заꙋ́тра, и҆ по́мощь и҆́хъ ѡ҆бетша́етъ во а҆́дѣ: ѿ сла́вы своеѧ̀ и҆зринове́ни бы́ша. Ѻ҆ба́че бг҃ъ и҆зба́витъ дꙋ́шꙋ мою̀ и҆з̾ рꙋкѝ а҆́довы, є҆гда̀ прїе́млетъ мѧ̀. Не ᲂу҆бо́йсѧ, є҆гда̀ разбогатѣ́етъ человѣ́къ, и҆лѝ є҆гда̀ ᲂу҆мно́житсѧ сла́ва до́мꙋ є҆гѡ̀: Ꙗ҆́кѡ внегда̀ ᲂу҆мре́ти є҆мꙋ̀, не во́зметъ всѧ̑, нижѐ сни́детъ съ ни́мъ сла́ва є҆гѡ̀. Ꙗ҆́кѡ дꙋша̀ є҆гѡ̀ въ животѣ̀ є҆гѡ̀ бл҃гослови́тсѧ, и҆сповѣ́стсѧ тебѣ̀, є҆гда̀ бл҃госотвори́ши є҆мꙋ̀. Вни́детъ да́же до ро́да ѻ҆тє́цъ свои́хъ, да́же до вѣ́ка не ᲂу҆́зритъ свѣ́та. И҆ человѣ́къ въ че́сти сы́й не разꙋмѣ̀, приложи́сѧ скотѡ́мъ несмы́слєннымъ и҆ ᲂу҆подо́бисѧ и҆̀мъ.",
