add_evangelie {
  ["зачало"] = 9,
  ["комментарий"] = "Мф., 9 зач., IV, 18–23.",
  ["текст_ру"] = "Во вре́мя о́но, ходя́ Иису́с при мо́ри Галиле́йстем, ви́де два бра́та, Си́мона глаго́лемаго Петра́, и Андре́а бра́та его́, вмета́юща мре́жи в мо́ре, бе́ста бо ры́баря. И глаго́ла и́ма: гряди́та по Мне, и сотворю́ вы ловца́ челове́ком. Она́ же а́бие оста́вльша мре́жи, по Нем идо́ста. И преше́д отту́ду, ви́де и́на два бра́та, Иа́кова Зеведе́ева, и Иоа́нна бра́та его́, в корабли́ с Зеведе́ом отце́м ею́, завязу́юща мре́жи своя́, и воззва́ я́. О́на же а́бие оста́вльша кора́бль и отца́ своего́, по Нем идо́ста. И прохожда́ше всю Галиле́ю Иису́с, учя́ на со́нмищих их и пропове́дая Ева́нгелие Ца́рствия, изцеля́я всяк неду́г и вся́ку я́зю в лю́дех. ",
  ["текст_цс"] = "Во вре́мѧ ѻ҆́но, ходѧ̀ і҆и҃съ при мо́ри галїле́йстѣмъ, ви́дѣ два̀ бра́та, сі́мѡна глаго́лемаго петра̀, и҆ а҆ндре́а бра́та є҆гѡ̀, вмета́юща мрє́жи въ мо́ре, бѣ́ста бо ры́барѧ. и҆ глаго́ла и҆́ма: грѧди́та по мнѣ̀, и҆ сотворю́ вы ловца̀ чл҃вѣ́кѡмъ. ѻ҆́на же а҆́бїе ѡ҆ста́вльша мрє́жи, по не́мъ и҆до́ста. и҆ прише́дъ ѿтꙋ́дꙋ, ви́дѣ и҆́на два̀ бра́та, і҆а́кѡва зеведе́ева и҆ і҆ѡа́нна бра́та є҆гѡ̀, въ кораблѝ съ зеведе́омъ ѻ҆тце́мъ є҆ю̀, завѧзꙋ́юща мрє́жи своѧ̀, и҆ воззва̀ ѧ҆̀. ѻ҆́на же а҆́бїе ѡ҆ста́вльша кора́бль и҆ ѻ҆тца̀ своего̀, по не́мъ и҆до́ста. и҆ прохожда́ше всю̀ галїле́ю і҆и҃съ, ᲂу҆чѧ̀ на со́нмищахъ и҆́хъ, и҆ проповѣ́даѧ є҆ѵⷢ҇лїе црⷭ҇твїѧ, и҆сцѣлѧ́ѧ всѧ́къ недꙋ́гъ, и҆ всѧ́кꙋ ꙗ҆́зю въ лю́дехъ. ",
}

add_evangelie {
  ["зачало"] = 10,
  ["комментарий"] = "Мф., 10 зач., IV, 25 – V, 12.",
  ["текст_ру"] = "Во вре́мя о́но, по Иису́се идо́ша наро́ди мно́зи от Галиле́и и десяти́ град, и от Иерусали́ма и Иуде́и, и со о́наго по́лу Иорда́на. Узре́в же наро́ды, взы́де на гору́, и се́дшу Ему́, приступи́ша к Нему́ ученицы́ Его́. И отве́рз уста́ Своя́, уча́ше их, глаго́ля: блаже́ни ни́щии ду́хом, я́ко тех есть Ца́рствие Небе́сное. Блаже́ни пла́чущии, я́ко ти́и уте́шатся. Блаже́ни кро́тцыи, я́ко ти́и насле́дят зе́млю. Блаже́ни а́лчущии и жа́ждущии пра́вды, я́ко ти́и насы́тятся. Блаже́ни ми́лостивии, я́ко ти́и поми́ловани бу́дут. Блаже́ни чи́стии се́рдцем, я́ко ти́и Бо́га у́зрят. Блаже́ни миротво́рцы, я́ко ти́и сы́нове Бо́жии нареку́тся. Блаже́ни изгна́ни пра́вды ра́ди, я́ко тех есть Ца́рствие Небе́сное. Блаже́ни есте́, егда́ поно́сят вам, и иждену́т, и реку́т всяк зол глаго́л на вы лжу́ще, Мене́ ра́ди. Ра́дуйтеся и весели́теся, я́ко мзда ва́ша мно́га на небесе́х. ",
  ["текст_цс"] = "Во вре́мѧ ѻ҆́но, по і҆и҃сѣ и҆до́ша наро́ди мно́зи ѿ галїле́и и҆ десѧтѝ гра́дъ, и҆ ѿ і҆ерⷭ҇ли́ма, и҆ і҆ꙋде́и, и҆ со ѻ҆́нагѡ по́лꙋ і҆ѻрда́на. ᲂу҆зрѣ́въ же наро́ды, взы́де на горꙋ̀: и҆ сѣ́дшꙋ є҆мꙋ̀ пристꙋпи́ша къ немꙋ̀ ᲂу҆чн҃цы̀ є҆гѡ̀. и҆ ѿве́рзъ ᲂу҆ста̀ своѧ̀, ᲂу҆ча́ше и҆̀хъ, глаго́лѧ: бл҃же́ни ни́щїи дх҃омъ: ꙗ҆́кѡ тѣ́хъ є҆́сть црⷭ҇твїе нбⷭ҇ное. бл҃же́ни пла́чꙋщїи: ꙗ҆́кѡ ті́и ᲂу҆тѣ́шатсѧ. бл҃же́ни кро́тцыи: ꙗ҆́кѡ ті́и наслѣ́дѧтъ зе́млю. бл҃же́ни а҆́лчꙋщїи и҆ жа́ждꙋщїи пра́вды: ꙗ҆́кѡ ті́и насы́тѧтсѧ. бл҃же́ни млⷭ҇тивїи: ꙗ҆́кѡ ті́и поми́ловани бꙋ́дꙋтъ. бл҃же́ни чи́стїи се́рдцемъ: ꙗ҆́кѡ ті́и бг҃а ᲂу҆́зрѧтъ. бл҃же́ни миротво́рцы: ꙗ҆́кѡ ті́и сн҃ове бж҃їи нарекꙋ́тсѧ. бл҃же́ни и҆згна́ни пра́вды ра́ди: ꙗ҆́кѡ тѣ́хъ є҆́сть црⷭ҇твїе нбⷭ҇ное. бл҃же́ни є҆стѐ, є҆гда̀ поно́сѧтъ ва́мъ, и҆ и҆жденꙋ́тъ, и҆ рекꙋ́тъ всѧ́къ ѕо́лъ глаго́лъ на вы̀ лжꙋ́ще, менѐ ра́ди: ра́дꙋйтесѧ, и҆ весели́тесѧ, ꙗ҆́кѡ мзда̀ ва́ша мно́га на нб҃сѣ́хъ. ",
}

