add_evangelie {
  ["зачало"] = 57,
  ["комментарий"] = "Мф., 57 зач., XIV, 1–13.",
  ["текст_ру"] = "Во вре́мя о́но, услы́шав И́род четвертовла́стник слух Иису́сов, рече́ отроко́м свои́м: Сей есть Иоа́нн Крести́тель, той воскре́се от ме́ртвых, и сего́ ра́ди си́лы де́ются о нем. И́род бо есмь Иоа́нна, связа́ его́, и всади́ в темни́цу, Иродиа́ды ра́ди жены́ Фили́ппа бра́та своего́. Глаго́лаше бо ему́ Иоа́нн: не досто́ит ти име́ти ея́. И хотя́ше его́ уби́ти, убоя́ся наро́да, зане́ я́ко проро́ка его́ име́яху. Дни же бы́вшу рождества́ И́родова, пляса́ дщи Иродиа́дина посреде́, и угоди́ И́родови. Те́мже и с кля́твою изрече́ ей да́ти, его́же а́ще воспро́сит. Она́ же нава́ждена ма́терию свое́ю, даждь ми, рече́, зде на блю́де главу́ Иоа́нна Крести́теля. И печа́лен бысть царь, кля́твы же ра́ди, и за возлежа́щих с ним, повеле́ да́ти ей. И посла́в усе́кну Иоа́нна в темни́це. И принесо́ша главу́ его́ на блю́де, и да́ша деви́це, и отнесе́ ма́тери свое́й. И присту́пльше ученицы́ его́, взя́ша те́ло его́, и погребо́ша е́, и прише́дше возвести́ша Иису́сови. И слы́шав Иису́с оты́де отту́ду в корабли́, в пу́сто ме́сто еди́н. И слы́шавше наро́ди, по Нем идо́ша пе́ши от градо́в. ",
  ["текст_цс"] = "Во вре́мѧ ѻ҆́но, ᲂу҆слы́шавъ и҆́рѡдъ четвертовла́стникъ слꙋ́хъ і҆и҃совъ, речѐ ѻ҆трокѡ́мъ свои́мъ: се́й є҆́сть і҆ѡа́ннъ крⷭ҇ти́тель, то́й воскре́се ѿ ме́ртвыхъ, и҆ сегѡ̀ ра́ди си̑лы дѣ́ютсѧ ѡ҆ не́мъ. и҆́рѡдъ бо є҆́мъ і҆ѡа́нна, свѧза̀ є҆го̀, и҆ всадѝ въ темни́цꙋ, и҆рѡдїа́ды ра́ди жены̀ фїлі́ппа бра́та своегѡ̀. глаго́лаше бо є҆мꙋ̀ і҆ѡа́ннъ: не досто́итъ тѝ и҆мѣ́ти є҆ѧ̀. и҆ хотѧ́ше є҆го̀ ᲂу҆би́ти, ᲂу҆боѧ́сѧ наро́да: занѐ ꙗ҆́кѡ проро́ка є҆го̀ и҆мѣ́ѧхꙋ. дни́ же бы́вшꙋ рождества̀ и҆́рѡдова, плѧса̀ дщѝ и҆рѡдїа́дина посредѣ̀, и҆ ᲂу҆годѝ и҆́рѡдови. тѣ́мже и҆ съ клѧ́твою и҆зречѐ є҆́й да́ти, є҆гѡ́же а҆́ще воспро́ситъ. ѻ҆на́ же нава́ждена ма́терїю свое́ю, да́ждь мѝ, речѐ, здѣ̀ на блю́дѣ главꙋ̀ і҆ѡа́нна крести́телѧ. и҆ печа́ленъ бы́сть ца́рь: клѧ́твы же ра́ди, и҆ за возлежа́щихъ съ ни́мъ, повелѣ̀ да́ти є҆́й. и҆ посла́въ ᲂу҆сѣ́кнꙋ і҆ѡа́нна въ темни́цѣ. и҆ принесо́ша главꙋ̀ є҆гѡ̀ на блю́дѣ, и҆ да́ша дѣви́цѣ: и҆ ѿнесѐ ма́тери свое́й. и҆ пристꙋ́пльше ᲂу҆ченицы̀ є҆гѡ̀, взѧ́ша тѣ́ло є҆гѡ̀, и҆ погребо́ша є҆̀: и҆ прише́дше возвѣсти́ша і҆и҃сови. и҆ слы́шавъ і҆и҃съ, ѿи́де ѿтꙋ́дꙋ въ кораблѝ, въ пꙋ́сто мѣ́сто є҆ди́нъ. и҆ слы́шавше наро́ди, по не́мъ и҆до́ша пѣ́ши ѿ градѡ́въ. ",
}

add_evangelie {
  ["зачало"] = 58,
  ["комментарий"] = "Мф., 58 зач., XIV, 14–22.",
  ["текст_ру"] = "Во вре́мя о́но, ви́де Иису́с мног наро́д, и милосе́рдова о них, и исцели́ неду́жныя их. По́зде же бы́вшу, приступи́ша к Нему́ ученицы́ Его́, глаго́люще: пу́сто есть ме́сто, и час уже́ мину́, отпусти́ наро́ды, да ше́дше в ве́си ку́пят бра́шна себе́. Иису́с же рече́ им: не тре́буют отити́, дади́те им вы я́сти. Они́ же глаго́лаша Ему́: не и́мамы зде то́кмо пять хлеб и две ры́бе. Он же рече́: принеси́те Ми их се́мо. И повеле́в наро́дом возлещи́ на траве́, и прие́м пять хлеб и о́бе ры́бе, воззре́в на не́бо, благослови́ и преломи́в даде́ ученико́м хле́бы, ученицы́ же наро́дом. И ядо́ша вси и насы́тишася, и взя́ша избы́тки укру́х, двана́десять ко́шя испо́лнь. Яду́щих же бе муже́й я́ко пять ты́сящ, ра́зве жен и дете́й. И а́бие пону́ди Иису́с ученики́ Своя́ вле́зти в кора́бль и вари́ти Его́ на о́ном полу́, до́ндеже отпу́стит наро́ды. ",
  ["текст_цс"] = "Во вре́мѧ ѻ҆́но, ви́дѣ і҆и҃съ мно́гъ наро́дъ, и҆ милосе́рдова ѡ҆ ни́хъ, и҆ и҆сцѣлѝ недꙋ̑жныѧ и҆́хъ. по́здѣ же бы́вшꙋ, пристꙋпи́ша къ немꙋ̀ ᲂу҆ченицы̀ є҆гѡ̀, глаго́люще: пꙋ́сто є҆́сть мѣ́сто, и҆ ча́съ ᲂу҆жѐ минꙋ̀: ѿпꙋстѝ наро́ды, да ше́дше въ вє́си, кꙋ́пѧтъ бра̑шна себѣ̀. і҆и҃съ же речѐ и҆̀мъ: не тре́бꙋютъ ѿитѝ, дади́те и҆̀мъ вы̀ ꙗ҆́сти. ѻ҆ни́ же глаго́лаша є҆мꙋ̀: не и҆́мамы здѣ̀ то́кмѡ пѧ́ть хлѣ̑бъ и҆ двѣ̀ ры́бѣ. ѻ҆́нъ же речѐ: принеси́те мѝ и҆̀хъ сѣ́мѡ. и҆ повелѣ́въ наро́дѡмъ возлещѝ на травѣ̀, и҆ прїе́мъ пѧ́ть хлѣ̑бъ и҆ ѻ҆́бѣ ры́бѣ, воззрѣ́въ на нб҃о, бл҃гословѝ: и҆ преломи́въ дадѐ ᲂу҆ченикѡ́мъ хлѣ́бы, ᲂу҆ченицы́ же наро́дѡмъ. и҆ ꙗ҆до́ша всѝ, и҆ насы́тишасѧ: и҆ взѧ́ша и҆збы́тки ᲂу҆крꙋ̑хъ, двана́десѧть ко́шѧ и҆спо́лнь. ꙗ҆дꙋ́щихъ же бѣ̀ мꙋже́й ꙗ҆́кѡ пѧ́ть ты́сѧщъ, ра́звѣ же́нъ и҆ дѣте́й. и҆ а҆́бїе понꙋ́ди і҆и҃съ ᲂу҆ченикѝ своѧ̀ влѣ́зти въ кора́бль, и҆ вари́ти є҆го̀ на ѻ҆́номъ полꙋ̀, до́ндеже ѿпꙋ́ститъ наро́ды. ",
